package com.demo.loop;

public class AddNum {
	
	public static void addNum() {
		int i,sum=0;
		for(i=1;i<=10;i++) {
			sum=sum+i;
			
		}
		System.out.println("Sum of 1 to 10 is "+sum);
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		addNum();
	}

}
